data:
int pts[] = primpoints(0, @primnum);
int npt = len(pts);
vector centerPos = 0;
int centerIndex = addpoint(0, centerPos);


for(int i = 0; i < npt; i++)
{
    int current = pts[i];
    int next = pts[(i + 1) % npt];
    addprim(0, "poly", centerIndex, current, next);
    centerPos += point(0, "P", pts[i]);
}

setpointattrib(0, "P", centerIndex, centerPos / npt);
removeprim(0, @primnum, 1);
